changeSet.sql:
create extension if not exists "uuid-ossp";

CREATE TABLE IF NOT EXISTS account (
    account_id uuid default uuid_generate_v4() not null
        constraint account_pkey
        primary key,
    country varchar(20) not null,
    customer_id varchar(10) not null
        constraint customer_id_uq
            unique
);

CREATE TABLE IF NOT EXISTS balance (
    account_id uuid not null,
    available_funds numeric(10, 2) not null,
    currency varchar(3) not null,
    constraint account_id_fk foreign key(account_id) references account(account_id)
);

create extension if not exists "uuid-ossp";
CREATE TABLE IF NOT EXISTS transaction ( account_id uuid not null, transaction_id uuid default uuid_generate_v4() not null constraint transaction_pkey primary key, amount numeric(10, 2) not null, currency varchar(3) not null, transaction_direction varchar(3) not null, description varchar(255) not null,
constraint account_id_fk foreign key(account_id) references account(account_id) );
